import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'
import { Form, Button, Row, Col} from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import Loader from "../components/Loader";
import Message from "../components/Message";
import FormContainer from '../components/FormContainer';
import { register } from '../actions/userActions'


function RegisterScreen({ location, history }) {
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [confirmPassword, setConfirmPassword] = useState('')
    const [message, setMessage] = useState('')

    const dispatch = useDispatch()

    const redirect = location.search ? location.search.split('=')[1] : '/'

    const userRegister = useSelector(state => state.userRegister)
    const {error, loading, userInfo} = userRegister

    useEffect(() => {
        if(userInfo){
            history.push(redirect)
        }
    }, [history, userInfo, redirect])

    const submitHandler = (e) => {
        e.preventDefault()

        if(password !== confirmPassword){
            setMessage('Passwords must match ')
        } else {
            dispatch(register(name, email, password))
        }
    }

    return (
        <FormContainer>
            <h1 className='text-center'>Register</h1>
            {message && <Message variant='danger'>{message}</Message>}
            {error && <Message variant='danger'>{error}</Message>}
            {loading && <Loader />}
            <Form onSubmit={submitHandler}>

                <Form.Group controlId='name'> 
                   <Form.Label>Name</Form.Label>
                   <Form.Control 
                        required
                        type='name'
                        placeholder='Enter your name'
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                   >
                   </Form.Control>
                </Form.Group>

                <Form.Group controlId='email' className='py-2'> 
                   <Form.Label>Email Adress</Form.Label>
                   <Form.Control
                        required
                        type='email'
                        placeholder='Enter Email'
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                   >
                   </Form.Control>
                </Form.Group>

                <Form.Group controlId='password' className='py-2'> 
                    <Form.Label>Password</Form.Label>
                    <Form.Control
                        required
                        type='password'
                        placeholder='Enter Password'
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    >
                   </Form.Control>
                </Form.Group>

                <Form.Group controlId='confirmPassword' className='py-2'> 
                   <Form.Label>Confirm password</Form.Label>
                    <Form.Control
                        required 
                        type='password'
                        placeholder='Confirm Password'
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                    >
                   </Form.Control>
                </Form.Group>

                <div className='text-center py-2'>
                    <Button type='submit' className='rounded'>Register</Button>
                </div>

            </Form>

            <Row className='text-center'>
                <Col>
                    Already have an account? <Link to={redirect ? `/login?redirect=${redirect}`: '/login' } className='text-link'>Sign In</Link>
                </Col>
            </Row>

        </FormContainer>
    )
}

export default RegisterScreen;
